from typing import Counter

class Solution:
  def checkInclusion(self, s1: str, s2: str) -> bool:
    ''' 
      Time complexity is O(n)
      Space complexity is O(1)
    '''
    if len(s2) < len(s1):
      return False
    
    freqS1 = {}
    freqS2 = {}
    for i in range(26):
      freqS1[chr(ord('a') + i)] = 0
      freqS2[chr(ord('a') + i)] = 0
    for i in s1:
      freqS1[i] += 1

    count = 0
    for end, ch in enumerate(s2):
      freqS2[ch] += 1
      count += 1
      if count > len(s1):
        freqS2[s2[end - count + 1]] -= 1
        count -= 1
      if freqS2 == freqS1:
        return True
    return False
    
sol = Solution()
assert sol.checkInclusion("ab", s2 = "eidbaooo") == True
assert sol.checkInclusion("ab", s2 = "eidboaoo") == False
assert sol.checkInclusion("ab", s2 = "eidboabo") == True
assert sol.checkInclusion("ab", s2 = "eidboabao") == True
assert sol.checkInclusion("abc", s2 = "eidbobaco") == True
assert sol.checkInclusion("abc", s2 = "eidbobaaco") == False
assert sol.checkInclusion("abc", s2 = "eidbobabaaco") == False
assert sol.checkInclusion("eibdobaaco", s2 = "abc") == False
assert sol.checkInclusion("aa", s2 = "aa") == True
assert sol.checkInclusion("adc", s2 = "dcda") == True
assert sol.checkInclusion(""
, "") == True
