from typing import List
from queue import PriorityQueue
from heapq import heappush, heappop

class Solution:
  def rangeSum(self, nums: List[int], n: int, left: int, right: int) -> int:
    MAX_VAL = 10**9 + 7
    pq = []
    for i in range(n):
      heappush(pq, [nums[i], i])
    ans = 0
    count = 0
    while len(pq) > 0:
      count += 1
      val, i = heappop(pq)
      if left <= count <= right:
        ans += val
      if i < n - 1:
        heappush(pq, [val + nums[i + 1], i + 1])
    return ans % MAX_VAL
  
  def rangeSum_PriorityQueue(self, nums: List[int], n: int, left: int, right: int) -> int:
    MAX_VAL = 10**9 + 7
    pq = PriorityQueue()
    for i in range(n):
      pq.put([nums[i], i])
    ans = 0
    count = 0
    while pq.qsize() > 0:
      count += 1
      val, i = pq.get()
      if left <= count <= right:
        ans += val
      if i < n - 1:
        pq.put([val + nums[i + 1], i + 1])
    # print(f'{ans}')
    return ans % MAX_VAL
sol = Solution()
assert sol.rangeSum([1,2,3,4], n = 4, left = 1, right = 5) == 13
assert sol.rangeSum([1,2,3,4], n = 4, left = 3, right = 4) == 6
assert sol.rangeSum([1,2,3,4], n = 4, left = 1, right = 10) == 50
